<script>import { helpStore } from "../../stores/pageStores";
export let id = "";
export let label = "";
export let required;
export let feedback;
export let help = false;
function onMouseOver() {
  if (help) {
    helpStore.show(id);
  }
}
function onMouseOut() {
}
</script>

<div id="{id}-container" on:mouseover={onMouseOver} on:mouseout={onMouseOut}>
	<label class="label">
		<span
			>{label}
			{#if required} <span class="text-sm text-red-600">*</span> {/if}
		</span>
		<slot />
		{#if feedback}
			<span class="text-sm text-error-600">
				<ul>
					{#each feedback as message}
						<li>{message}</li>
					{/each}
				</ul>
			</span>
		{/if}
	</label>
</div>
